<?php namespace LO\Controller;

use Silex\WebTestCase;
use Symfony\Component\HttpFoundation\Request;
use \LO\Model\Entity\RealtyCompany;

class RealtyCompanyControllerTest extends WebTestCase
{
    private $showDeprecated = false;

    public function createApplication() {
        $app = (new \LO\Application(['prod', 'test'], __DIR__.'/../../../../../../config/'))->bootstrap()->initRoutes();
        unset($app['exception_handler']);
        $app['session.test'] = true;
        $app['debug.test']   = true;
        $app->boot();

        return $app;
    }

    public function setUp() {
        parent::setUp();

        if (!$this->showDeprecated) {
            error_reporting(E_ALL ^ E_USER_DEPRECATED);
        }

        $this->app->getEntityManager()->getConnection()->executeQuery(
            file_get_contents(__DIR__.'/../../../../dump.sql')
        );
    }

    private function getPictureBase64()
    {
        return 'data:image/jpeg;base64,';
    }

    private function getCompanyData() {
        $company = new RealtyCompany();
        $company->setName('Test Name');
        $company->setLogo($this->getPictureBase64());

        return ['company' => $company->toArray()];
    }

    public function testAddCompany() {
        $controller = new Admin\RealtyCompanyController();
        $request    = new Request();

        $data = json_decode(
            $controller->addCompanyAction(
                $this->app,
                $request->create('/admin/realty', 'POST', $this->getCompanyData())
            )->getContent(),
            true
        );

        $this->assertEquals('33', $data['id']);
    }

    public function testEditCompany() {
        $idTest     = 32;
        $request    = new Request();
        $controller = new Admin\RealtyCompanyController();

        $company = $this->app->getEntityManager()->getRepository(RealtyCompany::class)->find($idTest);
        $company->setName('Test new name');
        $company->setLogo($this->getPictureBase64());

        $this->assertEquals(
            '"success"',
            $controller->updateCompanyAction(
                $this->app,
                $request->create('/admin/realty/'.$idTest, 'PUT', ['company' => $company->toArray()]),
                $idTest
            )->getContent()
        );
    }
}
